mapper.select:
SELECT sum(amount) from orders

     order_time > #{beginTime} 
     and order_time < #{endTime} 
     and status=#{status}

select  count(*) from user

     create_time > #{beginTime} 
     and create_time < #{endTime}

select  count(*) from orders

     order_time > #{beginTime} 
     and order_time < #{endTime} 
     and status=#{status}

select   od.name as name,count(*) as number
from  order_detail as od , orders d

 d.order_time > #{beginTime} 
 and d.order_time < #{endTime} 
 and status=#{status} 
    and d.id=od.order_id

group by  od.name  order by  count(*) desc limit 10;
